function hide(element) {
  document.getElementById(element).setAttribute("hidden", "hidden");
}

function show(element) {
  document.getElementById(element).removeAttribute("hidden");
}

function loadgroups() {
  hide("groups_button");
  hide("students_button");
  hide("students_content");
  show("groups_content");
  show("return_to_menu");
}

function loadstudents() {
  hide("groups_button");
  hide("students_button");
  hide("groups_content");
  hide("add_faculty");
  show("students_content");
  show("return_to_menu");
}

function returntomenu() {
  hide("students_content");
  hide("groups_content");
  hide("return_to_menu");
  show("add_faculty");
  show("groups_button");
  show("students_button");
}

function showform() {
  show("faculty_form");
  hide("add_faculty");
}

function hideform() {
  if (document.getElementById("students_content").getAttribute("hidden") == "hidden") show("add_faculty");
  hide("faculty_form");
}
